import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square(props) {
  return (
    <button className="square" style={props.style} onClick={props.onClick}>{props.value}</button>
  );
}

class Board extends React.Component {
  constructor(props) {
    super(props);

    const MAX_ROUNDS = 10;
    const SIGHT_TIER = ['Color-blind', 'Below Average', 'Average', 'Eagle', 'Robot']
    const ROUND_TIMER = 20000;

    this.state = {
      currentRound: 1,
      score: 0,
      answerSquare: 0,
      status: null,

      baseColor: 'rgb(251, 232, 106)',
      offColor: 'rgb(240, 230, 100)'
    }
  }

  nextRound() {
    // add pause and turn off touch

    // todo add difficulty
    let R = Math.floor(Math.random()*245);
    let G = Math.floor(Math.random()*245);
    let B = Math.floor(Math.random()*245);

    let answer = Math.floor(Math.random()*9);
    console.log(answer);

    this.setState({
      answerSquare: answer,
      baseColor: `rgb(${R}, ${G}, ${B})`,
      offColor: `rgb(${R+5}, ${G+5}, ${B+5})`
    });
  }

  handleClick(i) {
    let score = this.state.score;
    if (!this.checkAnswer(i)) {
      score++;
    }

    this.setState({
      status: 'correct',
      score: score,
      currentRound: this.state.currentRound+1
    }, this.nextRound());
  }

  // render with random color and offset one
  renderSquare(i) {
    const divStyle = {
      backgroundColor: i === this.state.answerSquare ? this.state.offColor : this.state.baseColor,
      borderColor: i === this.state.answerSquare ? this.state.offColor : this.state.baseColor
    };

    return (
      <Square 
        value={i}
        onClick={() => this.handleClick(i)}
        style={divStyle}
      />
    )
  }

  render() {
    let status = this.state.status ? 'Correct' : 'Find the odd';

    // @todo create more squares per round
    // @question how do we make the css flexible enough as squares grow
    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="board-row">
          {this.renderSquare(5)}
          {this.renderSquare(6)}
          {this.renderSquare(7)}
        </div>
        <div className="board-row">
          {this.renderSquare(9)}
          {this.renderSquare(10)}
          {this.renderSquare(11)}
        </div>
      </div>
    );
  }

  /** @todo
   * Check if clicked on was correct
   * */ 
  checkAnswer(i) {
    if (i === this.state.answerSquare) {
      console.log('correct');

      return true;
    } else {
      console.log('incorrect');
      return false;
    }
  }
}

// @todo refactor naming
class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);
